//change colour of header line div when scrolling past point

var scroll_pos = 0;
$(document).scroll(function () {
    scroll_pos = $(this).scrollTop();
    if (scroll_pos > 50) {
        $("#header-line").css('background-color', 'rgba(255,255,255,.15)');
    } else {
        $("#header-line").css('background-color', 'rgba(255,255,255,0)');
    }
});

//change size of logo header div when scrolling past point
$(function () {
    $('.logo-header').data('size', 'big');
});

$(window).scroll(function () {
    if ($(document).scrollTop() > 0) {
        if ($('.logo-header').data('size') == 'big') {
            $('.logo-header').data('size', 'small');
            $('.logo-header').stop().animate({
                height: '50px'
            }, 600);
        }
    }
    else {
        if ($('.logo-header').data('size') == 'small') {
            $('.logo-header').data('size', 'big');
            $('.logo-header').stop().animate({
                height: '80px'
            }, 600);
        }
    }
});

//change colour of header div when scrolling past point
var elemSelector = '#header'
	, tStart = 0 // Start transition this many pixels from the top
	, tEnd = 200   // End transition
	, cStart = [53, 53, 53, 0]  // Start color
	, cEnd = [53, 53, 53, 1]   // End color
	, cDiff = [
		cEnd[0] - cStart[0],
		cEnd[1] - cStart[1],
		cEnd[2] - cStart[2],
		cEnd[3] - cStart[3]
	];

scrollColor();
function scrollColor() {
    var p = ($(this).scrollTop() - tStart) / (tEnd - tStart); // % of transition
    p = Math.min(1, Math.max(0, p)); // Clamp to [0, 1]
    var cBg = [
        Math.round(cStart[0] + cDiff[0] * p),
        Math.round(cStart[1] + cDiff[1] * p),
        Math.round(cStart[2] + cDiff[2] * p),
        Math.round((cStart[3] + cDiff[3] * p) * 100) / 100
    ];
    $(elemSelector).css('background-color', 'rgba(' + cBg.join(',') + ')');
    $(elemSelector).css('background-position', 'left');
}
$(document).scroll(function () {
    scrollColor();
});

/*home screen bottom scroll button*/
$(window).scroll(function () {

    if ($(this).scrollTop() > 0) {
        $('.scrollDown').fadeOut();
    }
    else {
        $('.scrollDown').fadeIn();
    }
});
